import React from 'react';
import './App.css';
import './langSlider.css'
import paperplane from './assets/icons/001-paper-plane-1.svg'
import gh from './assets/icons/icons8-github.svg'
import li from './assets/icons/icons8-linkedin.svg'
import roles from './assets/roles.json';
import work from './assets/work.json'
import gb from './assets/icons/gb-flag.svg'
import fr from './assets/icons/fr-flag.svg'

import code from './assets/workIcons/icons8-code-file-50.png'
import data_icon from './assets/workIcons/icons8-data-configuration-50.png'
import doc from './assets/workIcons/icons8-empty-document-50.png'
import slides from './assets/workIcons/icons8-presentation-50.png'
import excel from './assets/workIcons/icons8-spreadsheet-50.png'
import strategy from './assets/workIcons/icons8-strategy-50.png'
import arrow from './assets/workIcons/icons8-down-button-100.png'


import hk from './assets/photos/HONGKONG.jpg'
import pa from './assets/photos/PARIS.jpg'
import stra from './assets/photos/STRASBOURG.jpg'
import syd from './assets/photos/SYDNEY.jpg'
import ldn from './assets/photos/LONDON.jpg'
import sea from './assets/photos/SEATTLE.jpg'

const en_prename = `Hi, I'm `
const fr_premane = `Je m'appelle `
const en_prebio = "And I'm a"
const fr_prebio = "Et je suis un"
const en_contact ="Contact me"
const fr_contact = "Contactez-moi"
const en_travel=" Where I've Been "
const fr_travel = "Mes Voyages"
const en_work = "What I've Done"
const fr_work="Mes Travailles"

function importAll(r) {
    return r.keys().map(r);
}
const images = require.context('./assets/photos/', false, /\.(png|jpe?g|svg)$/);
const photos = [hk, pa, stra, syd, ldn, sea]

class App extends React.Component {
    constructor(props) {
        super();
        this.state = {
            mode: 'day',
            lang: 'en',
            header: "",
            contact: "",
            index: 0,
            BGI: 0,
        }
    }
    makeRoleArray(){
       return this.state.lang === 'en' ? roles.roles.en : roles.roles.fr
    }
    determineLogo(T){
        switch (T){
            case "code":
                return code;
                break
            case "excel":
                return excel
            case "doc":
                return doc
            case "strategy":
                return strategy
            case "data":
                return data_icon
            case "presentation":
                return slides
            default:
                return excel
        }
    }
    makeWorkArray(){
        let arr = this.state.lang === 'en' ? work.work.en : work.work.fr
        let inner = []
        for(let i in arr){
            inner.push(<div key={`${i}work`} className='workListItem'><img src={this.determineLogo(arr[i].type)}/>{arr[i].title}</div>)
        }
        return <div className='workListContainer'>{inner}</div>
    }


    componentDidMount() {
        console.log(images)
        this.Words = setInterval(
            () => this.changeWords(),
            3000
        )
        this.Photos = setInterval(
            () => this.changePhotos(),
            4000
        )
    };

    changeWords(){
        const stop = this.makeRoleArray().length
        if (this.state.index+1 === stop ){
            this.setState({index: 0})
        } else {
            this.setState({index: this.state.index +1})
        }
    }
    changePhotos(){
        const stop = photos.length
        if (this.state.BGI+1 === stop ){
            this.setState({BGI: 0})
        } else {
            this.setState({BGI: this.state.BGI +1})
        }
    }
    componentWillUnmount(){
        this.Words.clear()
        this.Photos.clear()
    }

    changeMode = () => {
        if (this.state.mode === 'day' || "") {
            this.setState({mode: "night"});
            document.body.style = "background-color: black;"
        }
        else if (this.state.mode === 'night') {
            this.setState({mode: "day"});
            document.body.style = "background-color: white;"
        }
    };
    changeLang = () => {
        if (this.state.lang === 'en' || "") {
            this.setState({lang: "fr"});
        }
        else if (this.state.lang === 'fr') {
            this.setState({lang: "en"});
        }
    };

//{this.state.lang === 'en' ? `${roles.roles.en[this.state.index].header}`: `${roles.roles.fr[this.state.index].header}`}
    testToggle = {
        color: 'white',
        gridArea: 'toggle',
        display: 'inline-block',
        maxHeight: '75px',
        margin: '0',
        padding: '0',
    }
    render() {
        return (

            <div className='pageMax '>


                <div className='header'>
                    <div className='inline logo'>
                        <p className='minor'>{this.state.lang === 'en' ? en_prename : fr_premane}</p>
                        <p className='logoText'>
                            Alex
                        </p>

                    </div>
                    <div className='bio'>
                        <p className='minor'>{this.state.lang === 'en' ? `${en_prebio}${roles.roles.en[this.state.index].extraLetter} ` : `${fr_prebio}${roles.roles.fr[this.state.index].extraLetter} `}</p>
                        <p className='bioText'>
                            {this.state.lang === 'en' ? `${roles.roles.en[this.state.index].header}`: `${roles.roles.fr[this.state.index].header}`}
                        </p>
                    </div>
                    <div className='accentBox'> </div>

                    <div className='toggle'>

                        <label className="langSwitch ">
                            <input type="checkbox" onChange={this.changeLang}/>
                            <span className="langSlider round "> </span>
                        </label>

                        <a className={this.testToggle}>
                            <div className={this.testToggle}>
                                <img src={gb} className={this.testToggle}/>
                                /
                                <img src={fr} className={this.testToggle}/>
                            </div>
                        </a>

                    </div>
                    <a href="mailto:me@am.xyz?" className='contact'>
                        <div className="contact">
                            <div className="cl">
                                {this.state.lang === 'en' ? `${en_contact}` : `${fr_contact}`}
                            </div>
                            <div className="cr">
                                <img src={paperplane}/>
                            </div>

                        </div>
                    </a>

                </div>

                <div className="boxes">


                    <a className ='travel' href='#'>
                        <div className="travel feature" style={{background: `url(${photos[this.state.BGI]})`}}>
                            <mark>{this.state.lang === 'en' ? en_travel : fr_travel}</mark>
                        </div>

                    </a>

                    <a className='github' href="https://github.com/amyer3">
                        <div className="github feature">
                            <div>
                                <img src={gh}/>
                            </div>

                        </div>
                    </a>

                    <a className='linkedin' href="https://www.linkedin.com/in/alexjmyers/">
                        <div className="linkedin feature">
                            <div>
                                <img src={li}/>
                            </div>
                        </div>

                    </a>
                        <div className='work'>
                            <div className='upperWork'><p>{this.state.lang === 'en' ? en_work : fr_work}</p>
                            <img src={arrow}/>
                            </div>

                            <div className="list">
                                {this.makeWorkArray()}
                            </div>
                        </div>
                </div>
            </div>
        );
    }
}

export default App;
