import React from 'react';
import './App.css';
import './langSlider.css'
import paperplane from './assets/icons/001-paper-plane-1.svg'
import gh from './assets/icons/icons8-github.svg'
import li from './assets/icons/icons8-linkedin.svg'
import data from './assets/text.json'

class App extends React.Component {
    constructor(props) {
        super();
        this.state = {
            mode: 'day',
            lang: 'en',
            header: "",
            contact: "",
            index: 1
        }
    }
    makeArray(){
       return this.state.lang === 'en' ? [data.roles.en] : [data.roles.fr]
    }


    componentDidMount() {
       this.ID = setInterval(
           () => this.changeWords(),
           3000
       )
    };

    changeWords(){
        const stop = this.makeArray() -1
        if (this.state.index ++ === stop ){
            this.setState({index: 0})
        } else {
            this.setState({index: this.state.index++})
        }
    }
    componentWillUnmount(){this.ID.clear()}

    changeMode = () => {
        if (this.state.mode === 'day' || "") {
            this.setState({mode: "night"});
            document.body.style = "background-color: black;"
        }
        else if (this.state.mode === 'night') {
            this.setState({mode: "day"});
            document.body.style = "background-color: white;"
        }
    };
    changeLang = () => {
        if (this.state.lang === 'en' || "") {
            this.setState({lang: "fr"});
        }
        else if (this.state.lang === 'fr') {
            this.setState({lang: "en"});
        }
    };

    render() {
        return (

            <div className='pageMax '>


                <div className='header'>
                    <div className='inline logo'>
                        <p className='minor'>{this.state.lang === 'en' ? `My name is ` : `Je m'appelle `}</p>
                        <p className='logoText'>
                            Alex
                        </p>

                    </div>
                    <div className='bio'>
                        <p className='minor'>{this.state.lang === 'en' ? `And I'm a ` : `Et je suis un `}</p>
                        <p className='bioText'>
                            {this.state.lang === 'en' ? `${data.roles.en[this.state.index].header}`: `${data.roles.fr[this.state.index].header}`}
                        </p>
                    </div>
                    <div className='accentBox'> </div>

                    <div className='toggle'>

                        <label className="langSwitch ">
                            <input type="checkbox" onChange={this.changeLang}/>
                            <span className="langSlider round "> </span>
                        </label>

                    </div>
                    <a href="mailto:me@am.xyz?" className='contact'>
                        <div className="contact">
                            <div className="cl">
                                {this.state.lang === 'en' ? `Contact me for ${data.roles.en[this.state.index].contact}` : `Me contactez pour ${data.roles.fr[this.state.index].contact}`}
                            </div>
                            <div className="cr">
                                <img src={paperplane}/>
                            </div>

                        </div>
                    </a>

                </div>

                <div className="boxes">


                    <a className='travel' href='#'>
                        <div className="travel feature">
                            Where I've Been
                        </div>

                    </a>

                    <a className='github' href="https://github.com/amyer3">
                        <div className="github feature">
                            <div>
                                <img src={gh}/>
                            </div>

                        </div>
                    </a>

                    <a className='linkedin' href="https://www.linkedin.com/in/alexjmyers/">
                        <div className="linkedin feature">
                            <div>
                                <img src={li}/>
                            </div>
                        </div>

                    </a>
                    <a className='work' href='#'>
                        <div className="work feature">
                            What I've Done
                        </div>
                    </a>

                </div>
            </div>
        );
    }
}

export default App;
