import React from 'react'
import {
    ComposableMap,
    ZoomableGroup,
    Geographies,
    Geography,
    Markers,
    Marker
} from "react-simple-maps"
import paths from './topo110m.json'
import sf from '../assets/icons/travel/sf.svg'


const wrapperStyles = {
    width: "100%",
    maxWidth: 980,
    margin: "0 auto",
    textDecoration: 'none',
    backgroundColor: '#78D2FA'
}
const places = [
    {name: "SF", airport: "SFO", coordinates: [-122.4599, 37.7400], YMarkerOffset: -15},
    {name: "Chicago", airport: "ORD",coordinates: [-87.75005, 41.8299], YMarkerOffset: -15},
    {name: "Melbourne", airport: "MEL",coordinates: [144.9750, -37.8200], YMarkerOffset: 25},
    {name: "LA", airport: "LAX",coordinates: [-118.1799, 33.9899], YMarkerOffset: 15},
    {name: "New York", airport: "JFK",coordinates: [-73.98001, 40.7499], YMarkerOffset: 15},
    {name: "Sydney", airport: "SYD",coordinates: [151.1851, -33.920010], YMarkerOffset: -15},
    {name: "Hong Kong", airport: "HKG",coordinates: [114.18500, 22.3049], YMarkerOffset: 15},
    {name: "Macau", airport: "MFM",coordinates: [113.5439, 22.1987], YMarkerOffset: 15},
    {name: "Seattle", airport: "SEA",coordinates: [-122.3351, 47.6080], YMarkerOffset: -15},
    {name: "Cancun", airport: "CUN",coordinates: [-86.8515, 21.1619], YMarkerOffset: -10},
    {name: "London", airport: "LHR",coordinates: [0.1278, 51.5074], YMarkerOffset: -15},
    {name: "Paris", airport: "CDG",coordinates: [2.3522, 48.8566], YMarkerOffset: 15},
    {name: "MontrÃ©al", airport: "MTL",coordinates: [-73.5673, 45.5017], YMarkerOffset: -15},
    {name: "Las Vegas", airport: "LAS",coordinates: [-115.1398, 36.1699], YMarkerOffset: -15},
    {name: "Strasbourg", airport: "SXB",coordinates: [7.7521, 48.5734], YMarkerOffset: -15},
    {name: "Ensenada", airport: "ESE",coordinates: [-116.5964, 31.8667], YMarkerOffset: -15},
    {name: "Catalina", airport: "CIB",coordinates: [-118.4163, 33.3879], YMarkerOffset: -15},
    {name: "Ketchikan", airport: "KTN",coordinates: [-131.6461, 55.3422], YMarkerOffset: -15},
    {name: "Sitka", airport: "SIT",coordinates: [-135.3300, 57.0531], YMarkerOffset: -15},



]

const earthStyle = {
    fill: '#A5DC69',
}

export default class Globe extends React.Component {
    render() {
        return (
            <div style={wrapperStyles}>
                <ComposableMap
                    projectionConfig={{
                        scale: 200,
                        rotation: [-11, 0, 0],
                    }}
                    width={980}
                    height={551}
                    style={{
                        width: "100%",
                        height: "auto",
                    }}
                >
                    <ZoomableGroup center={[10, 15]} disablePanning>
                        <Geographies geography={paths}>
                            {(geographies, projection) =>
                                geographies.map((geography, i) =>
                                    geography.id !== "ATA" && (
                                        <Geography key={i} geography={geography} projection={projection} style={
                                            {
                                                default: {
                                                    fill: "#A5DC69",
                                                    stroke: "#607D8B",
                                                    strokeWidth: 3,
                                                    outline: "none"
                                                },
                                                hover: {
                                                    fill: "#A5DC69",
                                                    stroke: "#607D8B",
                                                    strokeWidth: 3,
                                                    outline: "none"
                                                },
                                                pressed: {
                                                    fill: "#A5DC69",
                                                    stroke: "#607D8B",
                                                    strokeWidth: 3,
                                                    outline: "none"
                                                },
                                            }
                                        }/>
                                    ))}
                        </Geographies>
                        <Markers>
                            {places.map((marker, i) => (
                                <Marker
                                    key={i}
                                    marker={marker}
                                    src={sf}
                                    style={{
                                        default: { stroke: "#455A64" },

                                    }}
                                >
                                    <circle
                                    r = {2}
                                    style={{
                                        fill: "red"
                                    }}
                                    >

                                    </circle>


                                </Marker>
                            ))}
                        </Markers>
                    </ZoomableGroup>
                </ComposableMap>
            </div>
        )
    }
}
