import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const widthRule = "(max-width: 768px)";
const notWidthRule = "(min-width: 767px)";

//Use this to pull all photos from a folder, and make an array from them
function importAll(r) {
    return r.keys().map(r);
}

const images = importAll(require.context('../assets/photos/', false, /\.(png|jpe?g)$/));

const PhotoGrid = styled.div`
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    margin-left: 5%;
    margin-right: 5%;
    margin-top: 10%;
    grid-row-gap: 10%;

    @media ${widthRule} {
        display: grid;
        grid-template-columns: 100%

    }
`;
const PhotoCard = styled.div`
    width: 100%;
    height: 100%;
    max-width: 400px;
    max-height: 400px;
    border-radius: 10px;
    overflow: hidden;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    background-image: url(${props => props.imgURL});

`;
const Photo = styled.img`
    height: 300px;
    width: auto;
    border-radius: inherit;
    image: fit;
`;
const Header = styled.div`

`;




let PhotoArray =
images.map(imgSRC => 
  <PhotoCard imgURL={imgSRC} />

  )

export default function PhotoDisplay(props) {

  var isMobileDevice = () =>
  typeof window.orientation !== "undefined" ||
  navigator.userAgent.indexOf("IEMobile") !== -1 ||
  window.innerWidth <= 768;

  return (
    <React.Suspense fallback={<span>Content Loading</span>}>
      <PhotoGrid>
        {PhotoArray}
      </PhotoGrid>
    </React.Suspense>

  );
}
