import React from 'react';
import './App.css';
import Logo from './logo/Logo'
import './modeSlider.css'
import './langSlider.css'
import paperplane from './assets/icons/002-paper-plane.svg'
import gh from './assets/icons/icons8-github.svg'
import li from './assets/icons/icons8-linkedin.svg'
import plane from './assets/icons/007-plane.svg'
import planeColor from './assets/icons/008-plane-1.svg'

const roles = ['Finance', 'Engineering', 'Baking', 'Consulting', 'Bad Life Advice']

class App extends React.Component {
    constructor(props) {
        super();
        this.state = {
            mode: 'day',
            lang: 'en'
        }
    }

    componentDidMount() {
    };

    changeMode = () => {
        if (this.state.mode === 'day' || "") {
            this.setState({mode: "night"});
            document.body.style = "background-color: black;"
        }
        else if (this.state.mode === 'night') {
            this.setState({mode: "day"});
            document.body.style = "background-color: white;"
        }
    };
    changeLang = () => {
        if (this.state.lang === 'en' || "") {
            this.setState({lang: "fr"});
        }
        else if (this.state.lang === 'fr') {
            this.setState({lang: "en"});
        }
    };

    render() {
        return (

            <div className='pageMax '>


                <div className='header'>
                    <div className='inline logo'>
                        <p>My name is </p>
                        <p className='logoText'>
                            Alex
                        </p>

                    </div>
                    <div className='bio'>
                        <p> and I'm a </p>
                        <p className='bioText'>
                            Frontend Engineer
                        </p>
                        <div className='accentBox'> </div>
                    </div>


                    <div className='toggle'>

                        <label className="switch">
                            <input type="checkbox" onChange={this.changeMode}/>
                            <span className="slider round"> </span>
                        </label>
                        <label className="langSwitch ">
                            <input type="checkbox" onChange={this.changeLang}/>
                            <span className="langSlider round "> </span>
                        </label>

                    </div>
                    <a href="mailto:me@am.xyz?" className='contact'>
                        <div className="contact">
                            <div className="cl">
                                {`Contact me`}
                            </div>
                            <div className="cr">
                                <img src={paperplane}/>
                            </div>

                        </div>
                    </a>

                </div>
                <div className="boxes">


                    <a className='travel' href='#'>
                        <div className="travel feature">
                            Where I've Been
                        </div>

                    </a>

                    <a className='github' href="https://github.com/amyer3">
                        <div className="github feature">
                            <div>
                                <img src={gh}/>
                            </div>

                        </div>
                    </a>

                    <a className='linkedin' href="https://www.linkedin.com/in/alexjmyers/">
                        <div className="linkedin feature">
                            <div>
                                <img src={li}/>
                            </div>
                        </div>

                    </a>
                    <a className='work' href='#'>
                        <div className="work feature">
                            What I've Done
                        </div>
                    </a>

                </div>
            </div>
        );
    }
}

export default App;
