import React from 'react'
import {
    ComposableMap,
    ZoomableGroup,
    Geographies,
    Geography,
    Markers,
    Marker
} from "react-simple-maps"
import paths from './paths.json'
import sf from '../assets/icons/travel/sf.svg'


const wrapperStyles = {
    width: "100%",
    maxWidth: 980,
    margin: "0 auto",
    textDecoration: 'none',
    backgroundColor: '#78D2FA'
}
const places = [
    {name: "SF", coordinates: [-122.4599, 37.7400], markerOffset: -15},
    {name: "Chicago", coordinates: [-87.75005, 41.8299], markerOffset: -15},
    {name: "Melbourne", coordinates: [144.9750, -37.8200], markerOffset: 25},
    {name: "LA", coordinates: [-118.1799, 33.9899], markerOffset: 15},
    {name: "New York", coordinates: [-73.98001, 40.7499], markerOffset: 15},
    {name: "Sydney", coordinates: [151.1851, -33.920010], markerOffset: -15},
    {name: "Hong Kong", coordinates: [114.18500, 22.3049], markerOffset: 15},
    {name: "Seattle", coordinates: [-122.3351, 47.6080], markerOffset: -15},
    {name: "Cancun", coordinates: [-86.8515, 21.1619], markerOffset: -10},
    {name: "London", coordinates: [0.1278, 51.5074], markerOffset: -15},
    {name: "Paris", coordinates: [2.3522, 48.8566], markerOffset: 15},
    {name: "Montréal", coordinates: [-73.5673, 45.5017], markerOffset: -15},


]

const earthStyle = {
    fill: '#A5DC69',
}

export default class Globe extends React.Component {
    render() {
        return (
            <div style={wrapperStyles}>
                <ComposableMap
                    projectionConfig={{
                        scale: 205,
                        rotation: [-11, 0, 0],
                    }}
                    width={980}
                    height={551}
                    style={{
                        width: "100%",
                        height: "auto",
                    }}
                >
                    <ZoomableGroup center={[0, 20]} disablePanning>
                        <Geographies geography={paths}>
                            {(geographies, projection) =>
                                geographies.map((geography, i) =>
                                    geography.id !== "ATA" && (
                                        <Geography key={i} geography={geography} projection={projection} style={
                                            {
                                                default: {
                                                    fill: "#A5DC69",
                                                    stroke: "#607D8B",
                                                    strokeWidth: 3,
                                                    outline: "none"
                                                },
                                                hover: {
                                                    fill: "#A5DC69",
                                                    stroke: "#607D8B",
                                                    strokeWidth: 3,
                                                    outline: "none"
                                                },
                                            }
                                        }/>
                                    ))}
                        </Geographies>
                        <Markers>
                            {places.map((marker, i) => (
                                <Marker
                                    key={i}
                                    marker={marker}
                                    src={sf}
                                    style={{
                                        default: {fill: "red"},

                                    }}
                                >
                                    <g transform="translate(-12, -24)">
                                        <path
                                            fill="none"
                                            strokeWidth="2"
                                            strokeLinecap="square"
                                            strokeMiterlimit="10"
                                            strokeLinejoin="miter"
                                            d="M20,9c0,4.9-8,13-8,13S4,13.9,4,9c0-5.1,4.1-8,8-8S20,3.9,20,9z"
                                        />
                                        <circle
                                            fill="none"
                                            strokeWidth="2"
                                            strokeLinecap="square"
                                            strokeMiterlimit="10"
                                            strokeLinejoin="miter"
                                            cx="12"
                                            cy="9"
                                            r="3"
                                        />
                                    </g>
                                    <text
                                        textAnchor="middle"
                                        y={marker.markerOffset}
                                        style={{
                                            fontFamily: "Roboto, sans-serif",
                                            fill: "white",
                                            backgroundColor: 'black'
                                        }}
                                    >
                                        {marker.name}
                                    </text>
                                </Marker>
                            ))}
                        </Markers>
                    </ZoomableGroup>
                </ComposableMap>
            </div>
        )
    }
}
